/* eslint-disable */

<template>
  <div class="container">
    <form action="" class="flight-search">
      <div class="city-input">
        <input
          type="text"
          list="city"
          v-model="departure"
          placeholder="Departure City"
        />
        <hr />
        <input
          type="text"
          list="city"
          v-model="arrival"
          placeholder="Arrival City"
        />
        <datalist id="city" class="recommend">
          <option v-for="city in cities" :key="city">
            {{ city.city_name }}
          </option>
        </datalist>
        <button @click.prevent="FlightSearch" class="search-button">
          Search for flight
        </button>
      </div>
    </form>
    <table class="flight-list" v-if="flightlist">
      <tr class="head">
        <th>Plane</th>
        <th>Departure City</th>
        <th>Arrival City</th>
        <th>Departure Date</th>
        <th>Arrival Date</th>
        <!-- <th>Departure Time</th>
        <th>Arrival Time</th> -->
        <th>Normal Seats</th>
        <!-- <th>Normal Seat Price</th> -->
        <th>Business Seats</th>
        <th></th>

        <!-- <th>Business Seat Price</th> -->
      </tr>
      <tr v-for="flight in flightlist" :key="flight">
        <th>{{ flight.planes }}</th>
        <th>{{ flight.departure_city }}</th>
        <th>{{ flight.arrival_city }}</th>
        <th>{{ formatDate(flight.departure_date) }}</th>
        <th>{{ formatDate(flight.arrival_date) }}</th>
        <!-- <th>{{ flight.departure_time }}</th>
        <th>{{ flight.arrival_time }}</th> -->
        <th>{{ flight.normal_seat }}</th>
        <th>{{ flight.business_seat }}</th>
        <!-- <th>{{ flight.normal_seat_price }}</th>
        <th>{{ flight.business_seat_price }}</th> -->
        <td>
          <button
            class="detail"
            @click="goToOrder(flight.flight_id)"
            :id="flight.flight_id"
          >
            Order Now
          </button>
        </td>
      </tr>
    </table>
    <div v-else>Find the flight in your desire</div>
  </div>
  <iframe
    src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3719.3309818186312!2d105.80198221535656!3d21.218719886641157!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3135021539afc453%3A0xe6e0faa3520226b4!2zQ-G6o25nIGjDoG5nIGtow7RuZyBxdeG7kWMgdOG6vyBO4buZaSBCw6Bp!5e0!3m2!1svi!2s!4v1679394147672!5m2!1svi!2s"
    width="600"
    height="450"
    style="border: 0"
    allowfullscreen=""
    loading="lazy"
    referrerpolicy="no-referrer-when-downgrade"
    class="map"
  ></iframe>
</template>

<script>
import /* FontAwesomeIcon */ "@fortawesome/vue-fontawesome";
import { ref, onMounted } from "vue";
import { faPlane } from "@fortawesome/free-solid-svg-icons";
import { library } from "@fortawesome/fontawesome-svg-core";
import router from "../router";

import api from "../api";
export default {
  components: {
    /* FontAwesomeIcon */
  },
  setup() {
    let departure = ref("");
    let arrival = ref("");
    let cities = ref();
    let flightlist = ref();
    library.add(faPlane);
    onMounted(() => {
      api
        .get("/cities")
        .then((response) => {
          cities.value = response.data;
        })
        .catch((error) => {
          console.error(error);
        });
    });
    function FlightSearch() {
      api
        .post("/flightsearch", {
          departure: departure.value,
          arrival: arrival.value,
        })
        .then((response) => {
          this.flightlist = response.data;
        })
        .catch((error) => {
          console.log(error);
        });
    }
    function formatDate(date) {
      let dateObj = new Date(date);
      const year = dateObj.getFullYear();
      const month = dateObj.getMonth() + 1;
      const day = dateObj.getDate();
      return `${year}-${month}-${day}`;
    }
    function goToOrder(id) {
      router.push({
        name: "order",
        query: {
          id: id,
        },
      });
    }
    return {
      departure,
      arrival,
      cities,
      FlightSearch,
      flightlist,
      formatDate,
      goToOrder,
    };
  },
};
</script>
<style scoped>
.map {
  width: 100%;
}
.container {
  padding-top: 10rem;
  background-image: url("../assets/image/wing.jpg");
  width: 100%;
  height: 55rem;
  display: flex;
  gap: 2rem;
  flex-direction: row;
}
.city-input {
  margin: auto;
  padding: auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-bottom: 1rem;
  gap: 1rem;
  width: 23rem;
  border-radius: 2rem;
  padding-top: 2rem;
  padding-bottom: 2rem;
  background-color: rgba(0, 0, 0, 0.4);
}
.city-input input {
  height: 3rem;
  width: 90%;
  border-radius: 0.5rem;
  padding-left: 0.5rem;
  border: 0.15rem solid rgb(51, 51, 51);
}
.flight-list {
  width: 100%;
  align-self: flex-start;
  margin-right: 2rem;
}
/* .recommend {
  width: 1000px;
} */
.flight-search {
  margin-left: 2rem;
  font-size: 3rem;
  color: rgb(255, 255, 255);
}
.search-button {
  border: none;
  border-radius: 1rem;
  padding: 0.5rem;
  height: 3rem;
}
hr {
  width: 90%;
}
table {
  border-collapse: collapse;
  font-size: 0.9em;
  font-family: sans-serif;
  min-width: 400px;
  box-shadow: 0 0 20px rgba(0, 0, 0, 0.15);
}
th,
td {
  vertical-align: top;
  border: 1px solid black;
  width: 100px;
  height: 3rem;
}
.head th {
  background-color: #ccdf98;
}
.detail {
  width: 100%;
  height: 100%;
  border: none;
}
</style>
