/* eslint-disable */

<template>
  <p>book here</p>
  <div class="container">
    <form action="" class="flight-search">
      <div class="city-input">
        <input
          type="text"
          list="city"
          v-model="departure"
          placeholder="Departure City"
        />
        <input
          type="text"
          list="city"
          v-model="arrival"
          placeholder="Arrival City"
        />
        <datalist id="city">
          <option v-for="city in responseData" :c>{{ city }}</option>
        </datalist>
        <button>Search for flight</button>
      </div>
    </form>
  </div>
</template>

<script>
import axios from "axios";
import { ref, onMounted } from "vue";
export default {
  components: {},
  setup() {
    let departure = ref("");
    let arrival = ref("");
    let cities = ref([]);
    let responseData = ref();
    onMounted(() => {
      axios
        .get("http://localhost:3000/cities")
        .then((response) => {
          responseData.value = response.data;
          console.log(responseData.value);
        })
        .catch((error) => {
          console.error(error);
        });
    });
    return {
      departure,
      arrival,
      cities,
      responseData,
    };
  },
};
</script>
<style>
::-webkit-datetime-edit {
  padding: 0.5em;
}
::-webkit-datetime-edit-fields-wrapper {
  background: silver;
}
::-webkit-datetime-edit-text {
  color: red;
  padding: 0 0.3em;
}
::-webkit-datetime-edit-month-field {
  color: blue;
}
::-webkit-datetime-edit-day-field {
  color: green;
}
::-webkit-datetime-edit-year-field {
  color: purple;
}
::-webkit-inner-spin-button {
  display: none;
}
:-webkit-calendar-picker-indicator {
  font-size: larger;
}
.city-input {
  margin: auto;
  padding: auto;
  display: flex;
  justify-content: center;
  margin-bottom: 1rem;
  gap: 2rem;
}
.city-input input {
  height: 3rem;
  width: 15rem;
}
</style>
