/* eslint-disable */

<template>
  <div class="container">
    <form action="" class="flight-search">
      <div class="city-input">
        <input
          type="text"
          list="city"
          v-model="departure"
          placeholder="Departure City"
        />
        <hr />
        <input
          type="text"
          list="city"
          v-model="arrival"
          placeholder="Arrival City"
        />
        <datalist id="city" class="recommend">
          <option v-for="city in cities" :key="city">
            {{ city.city_name }}
          </option>
        </datalist>
        <button @click.prevent="FlightSearch" class="search-button">
          Search for flight
        </button>
      </div>
    </form>
    <div class="flight-list" v-if="flightlist">
      <!-- <div class="head">
        <span>Plane</span>
        <span>Departure City</span>
        <span>Arrival City</span>
        <span>Departure Date</span>
        <span>Arrival Date</span>
        <span>Normal Seats</span>
        <span>Business Seats</span>
      </div> -->
      <div v-for="flight in flightlist" :key="flight" class="individual-flight">
        <span
          ><p>Plane:</p>
          <b>{{ flight.planes }}</b></span
        >
        <span
          ><p>Departure from:</p>
          <b>{{ flight.departure_city }}</b></span
        >
        <span
          ><p>Arrival to:</p>
          <b>{{ flight.arrival_city }}</b></span
        >
        <span
          ><p>Taking off date:</p>
          <b> {{ formatDate(flight.departure_date) }}</b></span
        >
        <span
          ><p>Landing date:</p>
          <b>{{ formatDate(flight.arrival_date) }}</b></span
        >
        <span
          ><p>Normal seats available:</p>
          <b>{{ flight.normal_seat }}</b></span
        >
        <span
          ><p>Business seats available:</p>
          <b> {{ flight.business_seat }}</b></span
        >
        <span>
          <button
            class="detail"
            @click="goToOrder(flight.flight_id)"
            :id="flight.flight_id"
          >
            Order Now
          </button>
        </span>
      </div>
    </div>
    <div v-else>Find the flight in your desire</div>
  </div>
  <iframe
    src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3719.3309818186312!2d105.80198221535656!3d21.218719886641157!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3135021539afc453%3A0xe6e0faa3520226b4!2zQ-G6o25nIGjDoG5nIGtow7RuZyBxdeG7kWMgdOG6vyBO4buZaSBCw6Bp!5e0!3m2!1svi!2s!4v1679394147672!5m2!1svi!2s"
    width="600"
    height="450"
    style="border: 0"
    allowfullscreen=""
    loading="lazy"
    referrerpolicy="no-referrer-when-downgrade"
    class="map"
  ></iframe>
</template>

<script>
import /* FontAwesomeIcon */ "@fortawesome/vue-fontawesome";
import { ref, onMounted } from "vue";
import { faPlane } from "@fortawesome/free-solid-svg-icons";
import { library } from "@fortawesome/fontawesome-svg-core";
import router from "../router";

import api from "../api";
export default {
  components: {
    /* FontAwesomeIcon */
  },
  setup() {
    let departure = ref("");
    let arrival = ref("");
    let cities = ref();
    let flightlist = ref();
    library.add(faPlane);
    onMounted(() => {
      api
        .get("/cities")
        .then((response) => {
          cities.value = response.data;
        })
        .catch((error) => {
          console.error(error);
        });
    });
    function FlightSearch() {
      api
        .post("/flightsearch", {
          departure: departure.value,
          arrival: arrival.value,
        })
        .then((response) => {
          this.flightlist = response.data;
        })
        .catch((error) => {
          console.log(error);
        });
    }
    function formatDate(date) {
      let dateObj = new Date(date);
      const year = dateObj.getFullYear();
      const month = dateObj.getMonth() + 1;
      const day = dateObj.getDate();
      return `${year}-${month}-${day}`;
    }
    function goToOrder(id) {
      router.push({
        name: "order",
        query: {
          id: id,
        },
      });
    }
    return {
      departure,
      arrival,
      cities,
      FlightSearch,
      flightlist,
      formatDate,
      goToOrder,
    };
  },
};
</script>
<style scoped>
.map {
  width: 100%;
}
.container {
  padding-top: 10rem;
  background-image: url("../assets/image/wing.jpg");
  width: 100%;
  height: 55rem;
  display: flex;
  gap: 2rem;
  flex-direction: row;
}
.city-input {
  margin: auto;
  padding: auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-bottom: 1rem;
  gap: 1rem;
  width: 23rem;
  border-radius: 2rem;
  padding-top: 2rem;
  padding-bottom: 2rem;
  background-color: rgba(0, 0, 0, 0.4);
}
.city-input input {
  height: 3rem;
  width: 90%;
  border-radius: 0.5rem;
  padding-left: 0.5rem;
  border: 0.15rem solid rgb(51, 51, 51);
}
.flight-list {
  width: 100%;
  align-self: flex-start;
  margin-right: 2rem;
}
/* .recommend {
  width: 1000px;
} */
.flight-search {
  margin-left: 2rem;
  font-size: 3rem;
  color: rgb(255, 255, 255);
}
.search-button {
  border: none;
  border-radius: 1rem;
  padding: 0.5rem;
  height: 3rem;
}
hr {
  width: 90%;
}
.individual-flight {
  display: flex;
  padding-left: 1rem;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  margin: 1rem;
  height: 5rem;
  background-color: whitesmoke;
  border-radius: 1rem;
  box-shadow: 5px 5px #c2c2c2;
}
.detail {
  width: 5rem;
  height: 5rem;
  border: none;
  background-color: aqua;
  border-radius: 1rem;
}
b {
  font-size: large;
}
</style>
