/* eslint-disable */
<template>
  <div class="top-bar">
    <nav class="nav-bar">
      <router-link to="/">Home |</router-link>
      <router-link to="/about">About |</router-link>
      <router-link to="/booking">
        Find Flight
        <font-awesome-icon icon="search" />
      </router-link>
    </nav>
    <nav class="user-section">
      <router-link to="/Login" v-if="!isLogin">Login |</router-link>
      <router-link to="/Register" v-if="!isLogin">Register |</router-link>
      <router-link to="/user" v-if="isLogin">Your Account</router-link>
      <router-link to="/admin" v-if="isLogin">Admin Panel</router-link>
      <a @click="logout">
        <font-awesome-icon :icon="['fa', 'right-from-bracket']" />
      </a>
    </nav>
  </div>
  <router-view />
  <!-- <button type="submit" @click="changestate">change</button> -->
  <confirm-box v-if="showConfirm"></confirm-box>
</template>
<script>
import { ref, onMounted } from "vue";
import { useStore } from "vuex";
import axios from "axios";
import VueCookies from "vue-cookie";
import { library } from "@fortawesome/fontawesome-svg-core";
import {
  faRightFromBracket,
  faSearch,
  faRightToBracket,
} from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/vue-fontawesome";
import ConfirmBox from "./components/ConfirmBox.vue";

export default {
  components: { FontAwesomeIcon, ConfirmBox },
  setup() {
    let store = useStore();
    let isLogin = ref(false);
    library.add(faRightFromBracket, faSearch, faRightToBracket);

    onMounted(() => {
      // console.log(this.token.value);
      axios
        .get("http://localhost:3000/check", { withCredentials: true })
        .then((response) => {
          if (response.data.isLogin === true) {
            store.commit("login");
            // console.log(store.state.isLogin);
            isLogin.value = store.state.isLogin;
          }
        })
        .catch((error) => {
          console.log(error);
        });
    });
    showConfirm

    // setTimeout(() => {
    //   console.log(isLogin.value);
    // }, 1000);
    return { isLogin, logout };
  },
};
</script>
<style>
#app {
  font-family: Avenir, Helvetica, Arial, sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  text-align: center;
  color: #2c3e50;
}
body {
  padding-top: 6rem;
}
* {
  margin: 0;
  padding: 0;
}
.top-bar {
  position: fixed;
  top: 0;
  width: 100%;
  height: 3.5rem;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  background-color: #42b983;

  align-items: center;
}
.user-section {
  font-size: x-large;
  display: flex;
  gap: 0.5rem;
  margin-right: 1rem;
}
.user-section * {
  font-size: x-large;
  font-weight: bold;
  color: #2c3e50;
  text-decoration: none;
}
.user-section *:hover {
  color: white;
}
.nav-bar {
  display: flex;
  gap: 0.5em;
  font-size: x-large;
}
.nav-bar *:hover {
  color: white;
}
nav a {
  font-size: x-large;
  font-weight: bold;
  color: #2c3e50;
  text-decoration: none;
}

nav a.router-link-exact-active {
  color: #ffffff;
}
</style>
