/* eslint-disable */

<template>
  <p>book here</p>
  <div class="container">
    <form action="" class="flight-search">
      <div class="city-input">
        <input
          type="text"
          list="city"
          v-model="departure"
          placeholder="Departure City"
        />
        <input
          type="text"
          list="city"
          v-model="arrival"
          placeholder="Arrival City"
        />
        <datalist id="city">
          <option v-for="city in cities" :key="city">
            {{ city.city_name }}
          </option>
        </datalist>
        <button @click.prevent="FlightSearch">Search for flight</button>
      </div>
    </form>
    <div class="flight-list">
      <p v-for="flight in flightlist" :key="flight">kdj{{ flight }}</p>
      <!-- <p>list {{ flightlist }}</p> -->
    </div>
  </div>
</template>

<script>
import axios from "axios";
import { ref, onMounted } from "vue";
export default {
  components: {},
  setup() {
    let departure = ref("");
    let arrival = ref("");
    let cities = ref();
    let flightlist = ref();
    onMounted(() => {
      axios
        .get("http://localhost:3000/cities")
        .then((response) => {
          cities.value = response.data;
          console.log(cities.value);
        })
        .catch((error) => {
          console.error(error);
        });
    });
    function FlightSearch() {
      axios
        .post("http://localhost:3000/flightsearch", {
          departure: departure.value,
          arrival: arrival.value,
        })
        .then((response) => {
          this.flightlist = response.data;
          console.log(this.flightlist);
        })
        .catch((error) => {
          console.log(error);
        });
    }
    return {
      departure,
      arrival,
      cities,
      FlightSearch,
      flightlist,
    };
  },
};
</script>
<style>
::-webkit-datetime-edit {
  padding: 0.5em;
}
::-webkit-datetime-edit-fields-wrapper {
  background: silver;
}
::-webkit-datetime-edit-text {
  color: red;
  padding: 0 0.3em;
}
::-webkit-datetime-edit-month-field {
  color: blue;
}
::-webkit-datetime-edit-day-field {
  color: green;
}
::-webkit-datetime-edit-year-field {
  color: purple;
}
::-webkit-inner-spin-button {
  display: none;
}
:-webkit-calendar-picker-indicator {
  font-size: larger;
}
.city-input {
  margin: auto;
  padding: auto;
  display: flex;
  justify-content: center;
  margin-bottom: 1rem;
  gap: 2rem;
}
.city-input input {
  height: 3rem;
  width: 15rem;
}
</style>
