<template>
  <p>book here</p>
  <div class="container">
    <form action="" class="flight-search">
      <div class="city-input">
        <input
          type="text"
          list="city"
          v-model="departure"
          placeholder="Departure City"
        />
        <input
          type="text"
          list="city"
          v-model="arrival"
          placeholder="Arrival City"
        />
        <datalist id="city">
          <option v-for="city in responseData">{{ city }}</option>
        </datalist>
        <button>Search for flight</button>
      </div>
    </form>
  </div>
</template>

<script>
import axios from "axios";
import { ref, onMounted } from "vue";
export default {
  components: {},
  setup() {
    let departure = ref("");
    let arrival = ref("");
    let cities = ref([]);
    let responseData = ref();
    onMounted(() => {
      axios
        .get("http://localhost:3000/cities")
        .then((response) => {
          responseData.value = response.data;
          console.log(responseData.value);
        })
        .catch((error) => {
          console.error(error);
        });
    });
    return {
      departure,
      arrival,
      cities,
      responseData,
    };
  },
};
</script>
