/* eslint-disable */

<template>
  <p>book here</p>
  <div class="container">
    <form action="" class="flight-search">
      <div class="city-input">
        <input
          type="text"
          list="city"
          v-model="departure"
          placeholder="Departure City"
        />
        <font-awesome-icon icon="fa-solid fa-arrow-right" class="point" /><input
          type="text"
          list="city"
          v-model="arrival"
          placeholder="Arrival City"
        />
        <datalist id="city" class="recommend">
          <option v-for="city in cities" :key="city">
            {{ city.city_name }}
          </option>
        </datalist>
        <button @click.prevent="FlightSearch">Search for flight</button>
      </div>
    </form>
    <table class="flight-list">
      <tr>
        <th>Plane</th>
        <th>Departure</th>
        <th>Arrival</th>
        <th>Fly Date</th>
        <th>Departure Time</th>
        <th>Seats</th>
      </tr>
      <tr v-for="flight in flightlist" :key="flight">
        <td>{{ flight.planes }}</td>
        <td>{{ flight.departure_city }}</td>
        <td>{{ flight.arrival_city }}</td>
        <td>{{ flight.go_date }}</td>
        <td>{{ flight.start_time }}</td>
        <td>{{ flight.seat }}</td>
      </tr>
    </table>
  </div>
</template>

<script>
import { library } from "@fortawesome/fontawesome-svg-core";
import { faArrowRight } from "@fortawesome/free-solid-svg-icons";
import axios from "axios";
import { FontAwesomeIcon } from "@fortawesome/vue-fontawesome";
import { ref, onMounted } from "vue";
export default {
  components: { FontAwesomeIcon },
  setup() {
    let departure = ref("");
    let arrival = ref("");
    let cities = ref();
    let flightlist = ref();
    library.add(faArrowRight);
    onMounted(() => {
      axios
        .get("http://localhost:3000/cities")
        .then((response) => {
          cities.value = response.data;
          console.log(cities.value);
        })
        .catch((error) => {
          console.error(error);
        });
    });
    function FlightSearch() {
      axios
        .post("http://localhost:3000/flightsearch", {
          departure: departure.value,
          arrival: arrival.value,
        })
        .then((response) => {
          this.flightlist = response.data;
          console.log(this.flightlist);
        })
        .catch((error) => {
          console.log(error);
        });
    }
    return {
      departure,
      arrival,
      cities,
      FlightSearch,
      flightlist,
    };
  },
};
</script>
