/* eslint-disable */

<template>
  <div class="container">
    <form action="" class="flight-search">
      <div class="city-input">
        <input
          type="text"
          list="city"
          v-model="departure"
          placeholder="Departure City"
        />
        <input
          type="text"
          list="city"
          v-model="arrival"
          placeholder="Arrival City"
        />
        <datalist id="city" class="recommend">
          <option v-for="city in cities" :key="city">
            {{ city.city_name }}
          </option>
        </datalist>
        <button @click.prevent="FlightSearch">Search for flight</button>
      </div>
    </form>
    <p><font-awesome-icon icon="fa-regular fa-plane" /></p>
    <table class="flight-list">
      <tr>
        <th>Plane</th>
        <th>Departure</th>
        <th>Arrival</th>
      </tr>
      <tr v-for="flight in flightlist" :key="flight">
        <td>{{ flight.planes }}</td>
        <td>{{ flight.departure_city }}</td>
        <td>{{ flight.arrival_city }}</td>
      </tr>
    </table>
  </div>
  <iframe
    src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3719.3309818186312!2d105.80198221535656!3d21.218719886641157!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3135021539afc453%3A0xe6e0faa3520226b4!2zQ-G6o25nIGjDoG5nIGtow7RuZyBxdeG7kWMgdOG6vyBO4buZaSBCw6Bp!5e0!3m2!1svi!2s!4v1679394147672!5m2!1svi!2s"
    width="600"
    height="450"
    style="border: 0"
    allowfullscreen=""
    loading="lazy"
    referrerpolicy="no-referrer-when-downgrade"
    class="map"
  ></iframe>
</template>

<script>
import { library } from "@fortawesome/fontawesome-svg-core";
import { FontAwesomeIcon } from "@fortawesome/vue-fontawesome";
import { ref, onMounted } from "vue";
import { faPlane } from '@fortawesome/free-regular-svg-icons'
import api from "../api";
export default {
  components: { FontAwesomeIcon },
  setup() {
    let departure = ref("");
    let arrival = ref("");
    let cities = ref();
    let flightlist = ref();
    library.add(faP);
    onMounted(() => {
      api
        .get("/cities")
        .then((response) => {
          cities.value = response.data;
          console.log(cities.value);
        })
        .catch((error) => {
          console.error(error);
        });
    });
    function FlightSearch() {
      api
        .post("/flightsearch", {
          departure: departure.value,
          arrival: arrival.value,
        })
        .then((response) => {
          this.flightlist = response.data;
          console.log(this.flightlist);
        })
        .catch((error) => {
          console.log(error);
        });
    }
    return {
      departure,
      arrival,
      cities,
      FlightSearch,
      flightlist,
    };
  },
};
</script>
<style scoped>
.map {
  width: 100%;
}
.container {
  background-image: url("../assets/image/wing.jpg");
  width: 100%;
  height: 55rem;
}
::-webkit-datetime-edit {
  padding: 0.5em;
}
::-webkit-datetime-edit-fields-wrapper {
  background: silver;
}
::-webkit-datetime-edit-text {
  color: red;
  padding: 0 0.3em;
}
::-webkit-datetime-edit-month-field {
  color: blue;
}
::-webkit-datetime-edit-day-field {
  color: green;
}
::-webkit-datetime-edit-year-field {
  color: purple;
}
::-webkit-inner-spin-button {
  display: none;
}
:-webkit-calendar-picker-indicator {
  font-size: larger;
}
.city-input {
  margin: auto;
  padding: auto;
  display: flex;
  justify-content: center;
  margin-bottom: 1rem;
  gap: 2rem;
}
.city-input input {
  height: 3rem;
  width: 15rem;
}
.flight-list {
  margin: auto;
  padding: auto;
  width: 50%;
}
.recommend {
  width: 1000px;
}
</style>
